var app=angular.module('PrommacApp',['ngRoute','ngAnimate','ngSanitize','ui.bootstrap']);
app.config(["$routeProvider",function($routeProvider){
	$routeProvider.when("/",{controller:"startController",templateUrl:"templates/start.html"}),
	$routeProvider.when("/news",{controller:"newsController",templateUrl:"templates/news.html"}),
	$routeProvider.when("/news/:name", {controller:"postController",templateUrl:"templates/post.html"}),
	$routeProvider.when("/media",{controller:"mediaController",templateUrl:"templates/media.html"}),
	$routeProvider.when("/about",{controller:"aboutController",templateUrl:"templates/about.html"}),
	$routeProvider.when("/players",{controller:"playersController",templateUrl:"templates/players.html"}),
	$routeProvider.when("/contact",{controller:"contactController",templateUrl:"templates/contact.html"}),
	$routeProvider.when("/fightcard",{controller:"fightcard",templateUrl:"templates/fightcard.html"}),
	$routeProvider.otherwise({redirectTo:"/"})}]);


app.controller("aboutController",["$scope",function($scope){}]);
app.controller("contactController",["$scope",function($scope){
 $scope.pageClass = 'contact'

}]);
app.controller("headerController",["$scope","$location", function($scope,$location){
	{$scope.isActive=function($scope){return $scope === $location.path()}}
}]);
app.controller("mainController",["$scope","$location", function($scope,$location){
	$scope.mobileToggle=function(){
        $scope.isActiveMobile = !$scope.isActiveMobile;
    };
}]);
 
app.controller("mediaController",["$scope",function(o){
  
}]);
app.controller("newsController",["$scope","$http","$log", function($scope,$http,$log)
	{$http({method:"GET",url:"data/posts.json"}).then(function($http)
	{$scope.posts=$http.data.posts,$scope.totalItems=$scope.posts.length},function($scope){}),
	$scope.pageClass = 'news',
	$scope.maxSize=10,
	$scope.sorting='title',
	$scope.reverse=true,
	$scope.sortBy = function(sorting) {
     	$scope.sorting = sorting;
     console.log('test');
    };
    $scope.setBegin = function(last) {
     	$scope.begin = (last * $scope.itemsPerPage);
    };
    $scope.begin = 0;
	$scope.itemsPerPage = 20;
}]);
app.controller("playersController",["$scope",function($scope,$log){
   $scope.pageClass = 'players'
}]);

app.controller("postController",["$scope","$http","$routeParams","$sce",function($scope,$http,$routeParams,$sce)
	{$http({method:"GET",url:"data/posts.json"}).then(function($http){var o=$http.data.posts,s=o.filter(function($scope)
		{return $scope.slug==$routeParams.name});
		if($scope.post=s[0],$scope.content=$sce.trustAsHtml(s[0].content),console.log(s[0].id),s[0].id>0)
		{var i=o.filter(function($scope)
		{return $scope.id==s[0].id-1});
		$scope.vsBefore=!0,$scope.before=i[0].slug}
		if(s[0].id<o.length-1)
		{var f=o.filter(function($scope){return $scope.id==parseInt(s[0].id)+1});
		$scope.vsAfter=!0,$scope.after=f[0].slug}},
		function($scope){})}]);
app.controller("startController",["$scope",function($scope)
  {
    $scope.pageClass= "start",
  	$scope.slides=[{id:"0",
	image:"images/img00.jpg",
	text:"Image 01",href:"#/players"},
	{id:"1",image:"images/img01.jpg",text:"Image 02",href:"#/news/lukasz-klingier"}],
	$scope.myInterval=1e3
}]);
app.directive('slider',['$timeout',function(e)
	{return{restrict:'AE',replace:!0,templateUrl:'templates/slider.html'}}]);

app.directive('toggleClass', function() {
  return {
      restrict: 'A',
      link: function(scope, element, attrs) {
          element.on('click', function() {
              element.toggleClass(attrs.toggleClass);
          });
      }
  };
});
app.directive('addClass', function() {
  return {
      restrict: 'A',
      link: function(scope, element, attrs) {
          element.on('click', function() {
              element.siblings().removeClass(attrs.addClass);
              element.addClass(attrs.addClass);
          });
      }
  };
});
app.directive('upTop', function() {
  return {
      restrict: 'AE',
      template:'<button class="upTop">up</button>',
      link: function(scope, element,attr) {
           element.on('click', function() {
                $("body").animate({scrollTop: document.getElementById(attr.where).offsetTop}, "slow");
          });
      }
  };
});
app.directive('svgIcon', function() {
  return {
    restrict: 'AE',
    templateUrl: function(elem, attr) {
      return 'svg/icon-' + attr.type + '.html';
    }
  };
});
app.directive('ratingIcons', function() {
  return {
    restrict: 'AE',
    templateUrl: function(elem, attr) {
      return 'svg/rating.html';
    }
  };
});
app.directive('countDown', function(){
   return {
    restrict: 'C',
    link:function(scope, element, attrs){
        var e = {},
        date = new Date(attrs.date),
        updateClock = function () {
            e.seconds = (date - new Date())/1000;
            e.timeTillEvent = {
              daysLeft: parseInt(e.seconds/86400),
              hoursLeft: parseInt(e.seconds%86400/3600),
              minutesLeft: parseInt(e.seconds%86400%3600/60),
              secondsLeft: parseInt(e.seconds%86400%3600%60)
            }
            element.text(e.timeTillEvent.daysLeft+'d '+e.timeTillEvent.hoursLeft+'h '+ e.timeTillEvent.minutesLeft + 'm ' +
             e.timeTillEvent.secondsLeft + 's ')
        };

        setInterval(function () {
           updateClock()
        }, 1000);
        
        updateClock();
    }
  };
});



